query IssuesTable($filterBy: IssueFilters, $first: Int, $after: String, $orderBy: IssueOrder) {
 issues: issuesV2(
     filterBy: $filterBy
     first: $first
     after: $after
     orderBy: $orderBy
 ) {
     nodes {
     id
     sourceRule {
         ... on Control {
         id
         name
         controlDescription: description
         resolutionRecommendation
         securitySubCategories {
             title
             category {
             id
             name
             framework {
                 id
                 name
             }
             }
         }
         }
         ... on CloudEventRule {
         id
         name
         cloudEventRuleDescription: description
         sourceType
         type
         }
         ... on CloudConfigurationRule {
         id
         name
         cloudConfigurationRuleDescription: description
         remediationInstructions
         serviceType
         }
     }
     createdAt
     updatedAt
     dueAt
     type
     resolvedAt
     statusChangedAt
     projects {
         id
         name
         businessUnit
         riskProfile {
         businessImpact
         }
     }
     status
     severity
     entitySnapshot {
         id
         type
         nativeType
         name
         status
         cloudPlatform
         cloudProviderURL
         providerId
         region
         resourceGroupExternalId
         subscriptionExternalId
         subscriptionName
         subscriptionTags
         tags
         createdAt
         externalId
     }
     serviceTickets {
         externalId
         name
         url
     }
     notes {
         createdAt
         updatedAt
         text
         user {
         name
         email
         }
         serviceAccount {
         name
         }
     }
     }
     pageInfo {
     hasNextPage
     endCursor
     }
 }
 }

    query AuditLogTable($first: Int $after: String $filterBy: AuditLogEntryFilters){
    auditLogEntries(first: $first after: $after filterBy: $filterBy) {
    nodes {
        __typename
        id
        action
        requestId
        status
        timestamp
        actionParameters
        userAgent
        sourceIP
        serviceAccount {
        id
        name
        }
        user {
        id
        name
        }
    }
    pageInfo {
        hasNextPage
        endCursor
    }
    }
}
