Value.value:
query IssuesTable(
                $filterBy: IssueFilters
                $first: Int
                $after: String
                $orderBy: IssueOrder
                ) {
                issues(
                    filterBy: $filterBy
                    first: $first
                    after: $after
                    orderBy: $orderBy
                ) {
                    nodes {
                    ...IssueDetails
                    }
                    pageInfo {
                    hasNextPage
                    endCursor
                    }
                    totalCount
                    informationalSeverityCount
                    lowSeverityCount
                    mediumSeverityCount
                    highSeverityCount
                    criticalSeverityCount
                    uniqueEntityCount
                }
                }

                fragment IssueDetails on Issue {
                id
                control {
                    id
                    name
                    query
                }
                createdAt
                updatedAt
                projects {
                    id
                    name
                    slug
                    businessUnit
                    riskProfile {
                    businessImpact
                    }
                }
                status
                severity
                entity {
                    id
                    name
                    type
                }
                entitySnapshot {
                    id
                    type
                    nativeType
                    name
                    subscriptionId
                    subscriptionExternalId
                    subscriptionName
                    resourceGroupId
                    resourceGroupExternalId
                    region
                    cloudPlatform
                    cloudProviderURL
                    providerId
                    status
                    tags
                    subscriptionTags
                }
                note
                serviceTicket {
                    externalId
                    name
                    url
                }
                serviceTickets {
                    externalId
                    name
                    url
                    action {
                    id
                    type
                    }
                }
                }
